import "./bookingFormHome.css"
// Calendar
import {DateRange} from "react-date-range";
import "react-date-range/dist/styles.css";
import "react-date-range/dist/theme/default.css";
import {format} from "date-fns";

import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faCalendarDays, faChildren, faPerson, faBed} from "@fortawesome/free-solid-svg-icons"; 

import {useState} from "react"; 

function BookingFormHome() {

// For the calendar
    const [openDate, setOpenDate] = useState(false)
    const [date, setDate] = useState([
        {
            startDate: new Date(),
            endDate: new Date(),
            key: 'selection'
        }
    ])

// For the adults
    const [openAdultsOptions, setOpenAdultsOptions] = useState(false)
    const [adultOptions, setAdultOptions] = useState(1)

// For the children
    const [openChildrenOptions, setOpenChildrenOptions] = useState(false)
    const [childrenOptions, setChildrenOptions] = useState(0)

// For the rooms
    const [openRoomsOptions, setOpenRoomsOptions] = useState(false)
    const [roomsOptions, setRoomsOptions] = useState(1)

  return (
    <div className="headerSearch">
        <div className="headerSearchItem">
            <FontAwesomeIcon icon = {faCalendarDays} className="headerIcon" />
            <span onClick = {() => setOpenDate(!openDate)} className="headerSearchText">{`${format(date[0].startDate, "dd.MM.yyyy")}`} to {`${format(date[0].endDate, "dd.MM.yyyy")}`}</span>
            {openDate && <DateRange editableDateInputs={true} onChange = {item => setDate([item.selection])} 
            moveRangeOnFirstSelection = {false} ranges = {date} className="date"/>}
        </div>

        <div className="headerSearchItem">
            <FontAwesomeIcon icon = {faPerson} className="headerIcon"/>
            <span className="headerSearchText"> {adultOptions} adult </span>
        </div>

        <div className="headerSearchItem">
            <FontAwesomeIcon icon = {faChildren} className="headerIcon"/>
            <span className="headerSearchText"> {childrenOptions} children</span>
        </div>

        <div className="headerSearchItem">
            <FontAwesomeIcon icon = {faBed} className="headerIcon"/>
            <span className="headerSearchText"> {roomOptions} room</span>
        </div>

        <div className="headerSearchItem">
            <button className="headerBtn">Search</button>
        </div>
    </div>
  )
}

export default BookingFormHome
