import {useState, useEffect} from "react";
import {toast} from "react-toastify";

// Redux
import {useSelector, useDispatch} from "react-redux";
import {getMe, updateMe, reset} from "../features/user/userSlice";

function Me() {
  const [updateUserInfo, setUpdateUserInfo] = useState(false)
  const {user, isLoading, isError, isSuccess, message} = useSelector((state) => state.user)
  // const {user, isLoading, isError, isSuccess, message} = useSelector((state) => state.auth)

  const dispatch = useDispatch();

  useEffect(() => {
    if(isError) 
      toast.error(message);
    dispatch(getMe());
    // dispatch(reset())
  }, [message, isError, isSuccess,dispatch]);
  
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [email, setEmail] = useState("");

  const onSubmit = (e) => {
    e.preventDefault();
    const userData = {firstname, lastname, email}
    dispatch(updateMe(userData))
    setUpdateUserInfo(!updateUserInfo)
  }

  while(!user)
    return "Loading";
  
  if(user) {
    console.log("Here")
    document.getElementById("firstname").value = user.firstname;
    document.getElementById("lastname").value = user.lastname;
    document.getElementById("email").value = user.email;
  }

  return (
    <>
      <form onSubmit={onSubmit}>
        <fieldset disabled = {!updateUserInfo}>
          <input type="text" id="firstname" name="firstname" value={firstname} placeholder="Enter your firstname." onChange={(e) => setFirstname(e.target.value)}  />
          <input type="text" id="lastname" name="lastname" value={user.lastname} placeholder="Enter your last name." onChange={(e) => setLastname(e.target.value)} />
          <input type="email" id="email" name="email" value={user.email} placeholder="Enter your email address." onChange={(e) => setEmail(e.target.value)} />
          {updateUserInfo && <button type="submit" onSubmit={onSubmit}>Submit</button>}
        </fieldset>
      </form>
        <button onClick = {() => setUpdateUserInfo(!updateUserInfo)}>
          {updateUserInfo ? 'Cancel' : 'Update your user information'}
        </button>
    </>
  )
}

export default Me
