import "./bookingFormHome.css"
// Calendar
import {DateRange} from "react-date-range";
import "react-date-range/dist/styles.css";
import "react-date-range/dist/theme/default.css";
import {format} from "date-fns";

import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faCalendarDays, faChildren, faPerson, faBed} from "@fortawesome/free-solid-svg-icons"; 

import {useState} from "react";
import {toast} from "react-toastify"; 

function BookingFormHome() {

// For the calendar
    const [openDate, setOpenDate] = useState(false)
    const [date, setDate] = useState([
        {
            startDate: new Date(),
            endDate: new Date(),
            key: 'selection'
        }
    ])

// For the adults
    const [openAdultsOptions, setOpenAdultsOptions] = useState(false)
    const [adultsOptions, setAdultsOptions] = useState(1)

// For the children
    const [openChildrenOptions, setOpenChildrenOptions] = useState(false)
    const [childrenOptions, setChildrenOptions] = useState(0)

// For the rooms
    const [openRoomsOptions, setOpenRoomsOptions] = useState(false)
    const [roomsOptions, setRoomsOptions] = useState(1)

const handleOptions = (name, operation) => {
    if(name == "adult" && operation == "d") {
        if(adultsOptions == 1)
            toast.warn("There must be at least one adult in the room.")
        else
            setAdultsOptions(adultsOptions-1);
    }
    else if(name == "adult" && operation == "i")
        setAdultsOptions(adultsOptions+1);
    else if(name == "child" && operation == "d") {
        if(childrenOptions == 0)
            toast.warn("0 is the minimum number of children in the room")
        else
            setChildrenOptions(childrenOptions-1);
    }
    else if(name == "child" && operation == "i")
        setChildrenOptions(childrenOptions+1);
    else if(name == "room" && operation == "d") {
        if(roomsOptions == 1)
            toast.warn("1 is the minimum number of rooms")
        else
            setRoomsOptions(roomsOptions-1);
    }
    else if(name == "room" && operation == "i")
        setRoomsOptions(roomsOptions+1);
}
  return (
    <div className="headerSearch">
        <div className="headerSearchItem">
            <FontAwesomeIcon icon = {faCalendarDays} className="headerIcon" />
            <span onClick = {() => setOpenDate(!openDate)} className="headerSearchText">{`${format(date[0].startDate, "dd.MM.yyyy")}`} to {`${format(date[0].endDate, "dd.MM.yyyy")}`}</span>
            {openDate && <DateRange editableDateInputs={true} onChange = {item => setDate([item.selection])} 
            moveRangeOnFirstSelection = {false} ranges = {date} className="date"/>}
        </div>

        <div className="headerSearchItem">
            <FontAwesomeIcon icon = {faPerson} className="headerIcon"/>
            <span className="headerSearchText"> {adultsOptions} {adultsOptions == 1 ? "adult" : "adults"} </span>
            <div className="options">
                <div className="optionItem">
                    <span className="optionText">Adult</span>
                    <div className="optionCounter">
                        <button className="optionCounterButton" onClick = {() => handleOptions("adult", "d")}>-</button>
                        <span className="optionCounterNumber"> {adultsOptions} </span>
                        <button className="optionCounterButton" onClick = {() => handleOptions("adult", "i")}>+</button>
                    </div>
                </div>
            </div>
        </div>

        <div className="headerSearchItem">
            <FontAwesomeIcon icon = {faChildren} className="headerIcon"/>
            <span className="headerSearchText"> {childrenOptions > 0 && childrenOptions} {childrenOptions == 1 ? "child" : ((childrenOptions == 0 ? "No child" : "children"))}</span>
            <div className="options">
                <div className="optionItem">
                    <span className="optionText">Child</span>
                    <div className="optionCounter">
                        <button className="optionCounterButton" onClick = {() => handleOptions("child", "d")}>-</button>
                        <span className="optionCounterNumber"> {childrenOptions} </span>
                        <button className="optionCounterButton" onClick = {() => handleOptions("child", "i")}>+</button>
                    </div>
                </div>
            </div>
        </div>

        <div className="headerSearchItem">
            <FontAwesomeIcon icon = {faBed} className="headerIcon"/>
            <span className="headerSearchText"> {roomsOptions} {roomsOptions == 1 ? "room" : "rooms"}</span>
            <div className="options">
                <div className="optionItem">
                    <span className="optionText">Room</span>
                    <div className="optionCounter">
                        <button className="optionCounterButton" onClick = {() => handleOptions("room", "d")}>-</button>
                        <span className="optionCounterNumber"> {roomsOptions} </span>
                        <button className="optionCounterButton" onClick = {() => handleOptions("room", "i")}>+</button>
                    </div>
                </div>
            </div>
        </div>

        <div className="headerSearchItem">
            <button className="headerBtn">Search</button>
        </div>
    </div>
  )
}

export default BookingFormHome
