import "./bookingFormHome.css"
// Calendar
import {DateRange} from "react-date-range";
import "react-date-range/dist/styles.css";
import "react-date-range/dist/theme/default.css";
import {format} from "date-fns";

import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faCalendarDays, faChildren, faPerson, faBed} from "@fortawesome/free-solid-svg-icons"; 

import {useState} from "react"; 

function BookingFormHome() {

    const [openDate, setOpenDate] = useState(false)

    const [date, setDate] = useState([
        {
            startDate: new Date(),
            endDate: new Date(),
            key: 'selection'
        }
    ])

  return (
    <div className="headerSearch">
        <div className="headerSearchItem">
            <FontAwesomeIcon icon = {faCalendarDays} className="headerIcon" />
            <span className="headerSearchText">{`${format(date[0].startDate, "dd.MM.yyyy")}`} to {`${format(date[0].endDate, "dd.MM.yyyy")}`}</span>
            <DateRange editableDateInputs={true} onChange = {item => setDate([item.selection])} 
            moveRangeOnFirstSelection = {false} ranges = {date} className="date"/>
        </div>

        <div className="headerSearchItem">
            <FontAwesomeIcon icon = {faPerson} className="headerIcon"/>
            <span className="headerSearchText">2 adults</span>
        </div>

        <div className="headerSearchItem">
            <FontAwesomeIcon icon = {faChildren} className="headerIcon"/>
            <span className="headerSearchText">0 children</span>
        </div>

        <div className="headerSearchItem">
            <FontAwesomeIcon icon = {faBed} className="headerIcon"/>
            <span className="headerSearchText">1 room</span>
        </div>

        <div className="headerSearchItem">
            <button className="headerBtn">Search</button>
        </div>
    </div>
  )
}

export default BookingFormHome
