import {useState, useEffect} from "react";

// Redux
import {useSelector, useDispatch} from "react-redux";
import {getMe, reset} from "../features/user/userSlice";

function Me() {

  const [formData, setFormData] = useState({
    firstname: "",
    lastname: "",
    email: ""
  })
  const [updateUserInfo, setUpdateUserInfo] = useState(false)
  const {firstname, lastname, email} = formData;
  const dispatch = useDispatch();
  const {user, isLoading, isError, isSuccess, message} = useSelector((state) => state.user)

  // useEffect(() => {
  //   dispatch(getMe())
  // }, [])

  const onChange = (e) => {
    setFormData((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value
    }))
  }

  console.log(updateUserInfo)

  return (
    <>
      <form>
        <fieldset disabled="true">
        <input type="text" id="firstname" name="firstname" value={firstname} placeholder="firstname" onChange={onChange}/>
        <input type="text" id="lastname" name="lastname" value={lastname} placeholder="lastname" onChange={onChange} />
        <input type="email" id="email" name="email" value={email} placeholder="email" onChange={onChange}/>
      </fieldset>
      </form>
        <button type="submit" onClick = {() => setUpdateUserInfo(!updateUserInfo)}>Update your user information</button>
    </>
  )
}

export default Me
