import "./reserve.css";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faCircleXmark} from "@fortawesome/free-solid-svg-icons";

// React general
import {useEffect} from "react";

// Redux general
import {useSelector, useDispatch} from "react-redux";

// Redux from booking store
import {resetFo, getSingleRoom} from "../../features/booking/bookingSlice";

function Reserve({setOpen, roomId}) {
    
    const {rooms, isLoadingFo, isSuccessFo} = useSelector((state) => state.booking)
    const dispatch = useDispatch();

    // console.log(roomId)

    useEffect(() => {
        dispatch(getSingleRoom(roomId))
    }, [])

    if(isLoadingFo)
        return <div>Loading...</div>

  return (
    <div className="reserve">
        <div className="rContainer">
            <FontAwesomeIcon icon={faCircleXmark} className="rClose" onClick={() => setOpen(false)}/>
            <span>Select your rooms:</span>
        </div>
        {rooms.roomNumbers.map(roomNumber => (
            <div className="room">
                <label> {roomNumber.number} </label>
                <input type="select" value={roomNumber._id}/>
            </div>
        ))}
    </div>
  )
}

export default Reserve
