import "./reserve.css";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faCircleXmark} from "@fortawesome/free-solid-svg-icons";

// React general
import {useState, useEffect} from "react";

// Redux general
import {useSelector, useDispatch} from "react-redux";

// Redux from booking store
import {resetFo, getSingleRoom} from "../../features/booking/bookingSlice";

function Reserve({setOpen, roomId}) {
    
    const {rooms, isLoadingFo, isSuccessFo} = useSelector((state) => state.booking)
    const dispatch = useDispatch();

    const [selectedRooms, setSelectedRooms] = useState([])

    useEffect(() => {
        dispatch(getSingleRoom(roomId))
    }, [])

    const handleSelect = (e) => {
        const selected = e.target.checked;
        const value = e.target.value;
        setSelectedRooms(checked ? [...selectedRooms, value] : selectedRooms.filter(item => item !== value)) 
    }

    if(isLoadingFo)
        return <div>Loading...</div>

  return (
    <div className="reserve">
        <div className="rContainer">
            <FontAwesomeIcon icon={faCircleXmark} className="rClose" onClick={() => setOpen(false)}/>
            <span>Select your rooms:</span>
        </div>
        {rooms.roomNumbers.map(roomNumber => (
            <div className="room">
                <label> {roomNumber.number} </label>
                <input type="checkbox" value={roomNumber._id} onChange={handleSelect}/>
            </div>
        ))}
    </div>
  )
}

export default Reserve
