import {useState, useEffect} from "react";
import {toast} from "react-toastify";

// Redux
import {useSelector, useDispatch} from "react-redux";
import {getMe, updateMe, reset} from "../features/user/userSlice";

function Me() {
  const [updateUserInfo, setUpdateUserInfo] = useState(false)
  const {userMe} = useSelector((state) => state.user)
  const {user, isLoading, isError, isSuccess, message} = useSelector((state) => state.auth)

  const dispatch = useDispatch();

  useEffect(() => {
    if(isError) 
      toast.error(message);
    // dispatch(getMe());
    dispatch(reset())
  }, [message, isError, isSuccess,dispatch]);

  const [formData, setFormData] = useState({
    // firstname: user ? user.firstname : "",
    // lastname: user ? user.lastname : "",
    // email: user ? user.email : ""
    firstname: updateFirstname(),
    lastname: "",
    email: "",
  })
  
  const {firstname, lastname, email} = formData;
  
  const onChange = (e) => {
    setFormData((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value
    }))
  }

  const updateFirstname = async () => {
    const firstname = await userMe.firstname;
    return firstname;
  }

  updateFirstname();

  // if(user) {
  //   document.getElementById("firstname").value = user.firstname;
  //   document.getElementById("lastname").value = user.lastname;
  //   document.getElementById("email").value = user.email;
  // }


  const onSubmit = (e) => {
    e.preventDefault();
    const userData = {firstname, lastname, email}
    // dispatch(updateMe(userData))
  }


  return (
    <>
      <form onSubmit={onSubmit}>
        <fieldset disabled = {!updateUserInfo}>
          <input type="text" id="firstname" name="firstname" value={firstname} placeholder="Enter your firstname." onChange={onChange}  />
          <input type="text" id="lastname" name="lastname" value={lastname} placeholder="Enter your last name." onChange={onChange} />
          <input type="email" id="email" name="email" value={email} placeholder="Enter your email address." onChange={onChange} />
          {updateUserInfo && <button type="submit" onSubmit={onSubmit}>Submit</button>}
        </fieldset>
      </form>
        <button onClick = {() => setUpdateUserInfo(!updateUserInfo)}>
          {updateUserInfo ? 'Cancel' : 'Update your user information'}
        </button>
    </>
  )
}

export default Me
