import {useState} from "react";

function ReviewItem({review, user}) {
  
  // Format the date
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const day = date.getDate();
    const month = date.getMonth() + 1;
    const year = date.getFullYear();
    const formattedDate = `${day < 10 ? '0' + day : day}.${month < 10 ? '0' + month : month}.${year}`;
    return formattedDate;
  }
  const createdAt = formatDate(review.createdAt);
  const updatedAt = formatDate(review.updatedAt);
  
  const [edit, setEdit] = useState(false)
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [rating, setRating] = useState("");

  const editReview = () => {
    setEdit(!edit);
    setTitle(review.title);
    setDescription(review.description);
    setRating(review.rating);
  }

  const onSubmit = (e) => {
    e.preventDefault();
  }

  return (
    <>
    
    {user && <button onClick = {editReview}> {edit ? "Cancel" : "Edit" } </button>}

    {edit &&
    <form onSubmit = {onSubmit}>
      <fieldset>
        <input type="text" value={title} onChange = {(e) => setTitle(e.target.value)} />
        <input type="number" value={rating} onChange = {(e) => setRating(e.target.value)} />
        <input type="text" value={description} onChange = {(e) => setDescription(e.target.description)} />
      </fieldset>
    </form> 
    
    }
    

    <section>
      <div>title: {review.title}</div>
      <div>description: {review.description}</div>
      <div>rating: {review.rating}</div>
      {!user && <div>user: {review.user.firstname} {review.user.lastname}</div>}
      <div>created at: {createdAt}</div>
      {createdAt !== updatedAt && <div>updated at: {updatedAt}</div>}
      
    </section>

    <br></br>
    </>
  )
}

export default ReviewItem
