import {useState, useEffect} from "react";

// Redux
import {useSelector, useDispatch} from "react-redux";
import {getMe, reset} from "../features/user/userSlice";

function Me() {
  const [updateUserInfo, setUpdateUserInfo] = useState(true)
  const {user, isLoading, isError, isSuccess, message} = useSelector((state) => state.user)

  const dispatch = useDispatch();
  dispatch(getMe());

  const [formData, setFormData] = useState({
    firstname: user.firstname ? user.firstname : "",
    lastname: user.lastname ? user.lastname : "",
    email: user.email ? user.email : ""
    // firstname: "",
    // lastname: "",
    // email: ""
  })
  const {firstname, lastname, email} = formData;
  

  const onChange = (e) => {
    setFormData((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value
    }))
  }

  const onSubmit = (e) => {
    e.preventDefault();
    const userData = {firstname, lastname, email}
    // dispatch(updateMyUser(userData))
  }

  return (
    <>
      <form>
        <fieldset disabled = {updateUserInfo}>
          <input type="text" id="firstname" name="firstname" value={firstname} placeholder="firstname" onChange={onChange}  />
          <input type="text" id="lastname" name="lastname" value={lastname} placeholder="lastname" onChange={onChange} />
          <input type="email" id="email" name="email" value={email} placeholder="email" onChange={onChange} />
          {!updateUserInfo && <button type="submit" onSubmit={onSubmit}>Submit</button>}
        </fieldset>
      </form>
        <button onClick = {() => setUpdateUserInfo(!updateUserInfo)}>Update your user information</button>
    </>
  )
}

export default Me
